from .__init__ import Finish

import os
from dotenv import load_dotenv

load_dotenv()  


def check_credentials(username, password):
    stored_username = os.getenv('LIBRARY_USERNAME')
    stored_password = os.getenv('LIBRARY_PASSWORD')
    return username == stored_username and password == stored_password


class Finish:
    def __init__(self, specification, type=None, class_=None, method=None, color=None, additional_info=None):
        self.specification = specification
        self.type = type
        self.class_ = class_
        self.method = method
        self.color = color
        self.additional_info = additional_info

    def __str__(self):
        parts = [self.specification]
        if self.type:
            parts.append(f"Type {self.type}")
        if self.class_:
            parts.append(f"Class {self.class_}")
        if self.method:
            parts.append(f"Method {self.method}")
        if self.color:
            parts.append(f"Color {self.color}")
        if self.additional_info:
            parts.append(f"({self.additional_info})")
        return " ".join(parts)


